    const usuarios = await Usuario.lista();

    const conversor = new ConversorUsuario(
      "json",
      req.acesso
        ? req.acesso.todos
          ? req.acesso.todos.atributos
          : req.acesso.apensasSeu.atributos
        : []
    );

    res.status(200).send(conversor.converter(usuarios));
  },

