const usuariosDao = require("./usuarios-dao");
const { InvalidArgumentError, NotFound } = require("../erros");
const validacoes = require("../validacoes-comuns");
const bcrypt = require("bcrypt");

/**
 * A classe Usuario é responsável por gerenciar todas as operações relacionadas a usuários.
 */
class Usuario {
  /**
   * O construtor recebe os dados de um usuário e os atribui a instância atual
   * @param {object} usuario
   */
  constructor(usuario) {
    this.id = usuario.id;
    this.nome = usuario.nome;
    this.email = usuario.email;
    this.senhaHash = usuario.senhaHash;
    this.emailVerificado = usuario.emailVerificado;
    this.cargo = usuario.cargo;

    this.valida();
  }

  /**
   * @throws {InvalidArgumentError} - esse erro ocorre quando um usuario com o mesmo email já está cadastrado.
   */
  async adiciona() {
    if (await usuariosDao.buscaPorEmail(this.email)) {
      throw new InvalidArgumentError("O usuário já existe!");
    }

    await usuariosDao.adiciona(this);
    const { id } = await usuariosDao.buscaPorEmail(this.email);
    this.id = id;
  }

  valida() {
    validacoes.campoStringNaoNulo(this.nome, "nome");
    validacoes.campoStringNaoNulo(this.email, "email");

    const cargosValidos = ["admin", "editor", "assinante"];
    if (cargosValidos.indexOf(this.cargo) === -1) {
      throw new InvalidArgumentError("Campo cargo inválido!");
    }
  }

  async verificaEmail() {
    this.emailVerificado = true;
    await usuariosDao.modificaEmailVerificado(this, this.emailVerificado);
  }

  async deleta() {
    return usuariosDao.deleta(this);
  }

  static async buscaPorId(id) {
    const usuario = await usuariosDao.buscaPorId(id);
    if (!usuario) {
      throw new NotFound("usuario");
    }

    return new Usuario(usuario);
  }

  static async buscaPorEmail(email) {
    const usuario = await usuariosDao.buscaPorEmail(email);
    if (!usuario) {
      throw new NotFound("usuario");
    }

    return new Usuario(usuario);
  }

  static lista() {
    return usuariosDao.lista();
  }

  static gerarSenha(senha) {
    const custoHash = 12;
    return bcrypt.hash(senha, custoHash);
  }

  async adicionaSenha(senha) {
    validacoes.campoStringNaoNulo(senha, "senha");
    validacoes.campoTamanhoMinimo(senha, "senha", 8);
    validacoes.campoTamanhoMaximo(senha, "senha", 64);

    this.senhaHash = await Usuario.gerarSenha(senha);
  }
}

module.exports = Usuario;

const usuariosControlador = require("./usuarios-controlador");
const middlewaresAutenticacao = require("./middlewares-autenticacao");
const autorizacao = require("../middlewares/autorizacao");

module.exports = (app) => {
  app
    .route("/usuario/atualiza-token")
    .post(middlewaresAutenticacao.refresh, usuariosControlador.login);

  app
    .route("/usuario/login")
    .post(middlewaresAutenticacao.local, usuariosControlador.login);

  //O Refresh irá invalidar o refresh token, e o bearer irá autenticar o jwt.
  //Em seguida, o logout invalida o jwt, adicionando a blacklist
  app
    .route("/usuario/logout")
    .post(
      [middlewaresAutenticacao.refresh, middlewaresAutenticacao.bearer],
      usuariosControlador.logout
    );

  app
    .route("/usuario/verifica-email/:token")
    .get(
      middlewaresAutenticacao.verificacaoEmail,
      usuariosControlador.verificaEmail
    );

  app
    .route("/usuario")
    .post(usuariosControlador.adiciona)
    .get(
      [middlewaresAutenticacao.bearer, autorizacao("usuario", "ler")],
      usuariosControlador.lista
    );

  app
    .route("/usuario/:id")
    .delete(
      [
        middlewaresAutenticacao.bearer,
        middlewaresAutenticacao.local,
        autorizacao("usuario", "remover"),
      ],
      usuariosControlador.deleta
    );
};

const { InvalidArgumentError } = require('./erros');


module.exports = {
  campoStringNaoNulo: (valor, nome) => {
    if (typeof valor !== 'string' || valor === 0)
      throw new InvalidArgumentError(`É necessário preencher o campo ${nome}!`);
  },

  campoTamanhoMinimo: (valor, nome, minimo) => {
    if (valor.length < minimo)
      throw new InvalidArgumentError(
        `O campo ${nome} precisa ser maior que ${minimo} caracteres!`
      );
  },

  campoTamanhoMaximo: (valor, nome, maximo) => {
    if (valor.length > maximo)
      throw new InvalidArgumentError(
        `O campo ${nome} precisa ser menor que ${maximo} caracteres!`
      );
  }
};
